mapper.select:
SELECT COUNT(*)
FROM MEET_BOARD
WHERE S_NO = #{s_no}


SELECT *
FROM
(
	SELECT ROWNUM N, MEET_NO, MEET_REGDATE, MEET_ADDR, MEET_ADDR_DETAIL, MEET_TITLE, MEET_CONTENT, MEET_WRITER, VOTE_STARTDATE, VOTE_ENDDATE
	FROM MEET_BOARD
	WHERE S_NO = #{studyno}
	ORDER BY MEET_NO DESC 
)
WHERE N >= #{startRow} AND N <= #{endRow}


SELECT *
FROM
(
	SELECT ROWNUM N, MEET_NO, MEET_REGDATE, MEET_ADDR, MEET_ADDR_DETAIL, MEET_TITLE, MEET_CONTENT, MEET_WRITER, VOTE_STARTDATE, VOTE_ENDDATE
	FROM MEET_BOARD
	WHERE 1=1
	AND S_NO = #{studyno}



	ORDER BY MEET_NO DESC 
)
WHERE N >= #{startRow} AND N <= #{endRow}

mapper.update:
UPDATE MEET_BOARD
SET MEET_CNT = MEET_CNT + 1
WHERE MEET_NO = #{meet_no}

mapper.select:
SELECT * 
FROM MEET_BOARD
WHERE S_NO = #{s_no} AND MEET_TITLE = #{meet_title} AND VOTE_STARTDATE = #{vote_startdate} AND VOTE_ENDDATE = #{vote_enddate}

SELECT COUNT(VOTE_ATTEND)
FROM MEET_VOTE 
WHERE MEET_NO = #{meet_no}

SELECT COUNT(MEM_NO)
FROM MEET_VOTE
WHERE MEET_NO = #{meet_no}
AND MEM_NO = #{mem_no}
